import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
	  userorgid:null,//机构id
	  username:'',//用户名
	  role_id:'',//身份

  },
  getters: {
  },
  mutations: {
	  USER_IMG(state,user){
		  state.userorgid=user.UserOrgID
		  state.username=user.username
		  state.role_id=user.role_id
		  state.avatar=user.avatar
		  state.nickname=user.nickname
	  }
  },
  actions: {
  },
  modules: {
  }
})
