export default {
  provide() {
    //刷新
    return {
      reload: this.reload
    };
  },

  data() {
    return {
      isRouterAlive: true,
      mainheiget: ''
    };
  },

  created() {
    if (/(iPhone|iPad|iPod|iOS)/i.test(navigator.userAgent)) {
      // @ts-ignore
      window.getSafeAreaHeight.onmessage = function (res) {
        this.mainheiget = '0px';

        if (res != undefined) {
          this.mainheiget = JSON.parse(res.data).statusBarHeight + 'px';
          console.log(this.mainheiget);
        }
      }; // @ts-ignore


      window.getSafeAreaHeight.postMessage(); // @ts-ignore

      window.getSafeAreaHeight.onmessage?.();
    } else if (/(Android)/i.test(navigator.userAgent)) {
      // @ts-ignore：
      window.flutter_inappwebview.callHandler('getSafeAreaHeight').then(res => {
        if (res != undefined) {
          this.mainheiget = res.statusBarHeight + 'px'; // console.log(this.mainheiget);
        }
      });
    }
  },

  methods: {
    reload() {
      this.isRouterAlive = false;
      this.$nextTick(function () {
        this.isRouterAlive = true;
      });
    }

  },
  watch: {
    mainheiget(n) {
      console.log(n);
    },

    deep: true,
    // 深度监听
    immediate: true
  }
};
