import Popupp from '../Popupp.vue';
import http from '@/common/api/request.js';
import { Toast } from 'mint-ui';
export default {
  data() {
    return {
      addShow: false,
      eqType: '',
      //新增直播设备-设备类型
      eqType1: false,
      eqType2: true,
      popupVisible1: false,
      popupVisible2: false,
      popupVisible3: false,
      popupVisible4: false,
      serchInput: ''
    };
  },

  props: ['send'],
  components: {
    Popupp
  },
  methods: {
    // addList
    showTab(ss) {
      if (this.addShow) {
        this.addShow = false;
      } else {
        this.addShow = true;
      }
    },

    // 新增直播设备
    addEquipment() {
      this.popupVisible1 = true;
    },

    chooseEq() {
      if (this.eqType == '收银机') {
        this.eqType1 = false;
        this.eqType2 = true;
      } else {
        this.eqType1 = true;
        this.eqType2 = false;
      }
    },

    // 新增工控作业
    addEquipment2() {
      this.popupVisible2 = true;
    },

    // 新增工控服务
    addEquipment3() {
      this.popupVisible3 = true;
    },

    // 采集服务管理
    addEquipment4() {
      this.popupVisible4 = true;
    },

    closepop() {
      this.popupVisible1 = false;
      this.popupVisible2 = false;
      this.popupVisible3 = false;
      this.popupVisible4 = false;
      this.addShow = false;
    },

    // 搜索
    goSerch(t) {
      http.$axios({
        url: 'device/getDevices',
        method: 'get',
        params: {
          "org_id": Number(this.$store.state.userorgid),
          "device_name": t
        }
      }).then(res => {
        // console.log(res)
        this.$router.push({
          name: 'video',
          query: {
            'url': res.data.data[0]
          }
        });
      }).catch(() => {
        Toast("未搜索到该名称的设备");
      });
    },

    goback() {
      this.$router.back();
    }

  },
  watch: {
    eqType(newval, oldval) {
      if (newval == 2) {
        this.eqType = true;
        this.eqType2 = false;
      } else if (newval == 1) {
        this.eqType = false;
        this.eqType2 = true;
      }
    },

    addShow(val, oldval) {
      this.$emit('addnew', val);
    },

    popupVisible1(val) {
      this.$emit('pop1', val);
    },

    popupVisible2(val) {
      this.$emit('pop2', val);
    },

    popupVisible3(val) {
      this.$emit('pop3', val);
    },

    popupVisible4(val) {
      this.$emit('pop4', val);
    }

  }
};
