import request from '@/common/api/devrequest'





// 获取用户信息
export function userinfor(data){
	return request({
		url:'/user/'+data.org_id+"/"+data.username ,
	})
}

// 普通超级管理员添加组织
export function addadmin(data){
  return request({
    url:"/user",
    method:'post',
    data
  })
}
