import http from '@/common/api/request.js'; //插件

import BetterScroll from '@better-scroll/core';
export default {
  props: ['Exception'],
  data: function data() {
    return {
      selected: '1',
      ExceptionList: [],
      aftertodyList: [],
      yesterdayList: []
    };
  },
  methods: {
    gounnormal: function gounnormal(item) {
      var url1 = item.Videos[0].VideoUrl;
      var url2 = item.Videos[1].VideoUrl;
      var remark = item.Remark;
      var jobName = item.JobName;
      var createAt = item.CreateAt;
      var createBy = item.CreateBy;
      this.$router.push({
        path: "/unnormal",
        query: {
          'url1': url1,
          'url2': url2,
          'remark': remark,
          'jobName': jobName,
          'createAt': createAt,
          'createBy': createBy
        }
      });
    },
    aaa: function aaa() {
      console.log(this.$refs.wapp.style);
    }
  },
  created: function created() {
    var _this = this;

    // 今天获取工控服务列表
    http.$axios({
      url: 'job/getJobs',
      method: 'GET',
      params: {
        "org_id": this.$store.state.userorgid,
        "type": 1
      }
    }).then(function (res) {
      _this.ExceptionList = res.data.data;
    }).catch(function () {
      _this.ExceptionList = [];
    }), // 昨天获取工控服务列表
    http.$axios({
      url: 'job/getJobs',
      method: 'GET',
      params: {
        "org_id": this.$store.state.userorgid,
        "type": 2
      }
    }).then(function (res) {
      _this.aftertodyList = res.data.data;
    }).catch(function () {
      _this.aftertodyList = [];
    }); // 昨天之前获取工控服务列表

    http.$axios({
      url: 'job/getJobs',
      method: 'GET',
      params: {
        "org_id": this.$store.state.userorgid,
        "type": 3
      }
    }).then(function (res) {
      _this.yesterdayList = res.data.data;
    }).catch(function () {
      _this.yesterdayList = [];
    });
  },
  watch: {
    ExceptionList: function ExceptionList() {
      this.$nextTick(function () {
        new BetterScroll('.wrapper', {
          movable: true,
          zoom: true,
          probeType: 2,
          click: true
        });
      });
    },
    selected: function selected(n) {
      if (n == '2') {
        this.$nextTick(function () {
          new BetterScroll('.wrapper2', {
            movable: true,
            zoom: true,
            probeType: 2,
            click: true
          });
        });
      } else if (n == '3') {
        this.$nextTick(function () {
          new BetterScroll('.wrapper3', {
            movable: true,
            zoom: true,
            probeType: 2,
            click: true
          });
        });
      }
    }
  }
};
