import Serch from '@/components/home/Serch.vue';
import Camera from '@/components/home/Camera.vue';
import Abnormal from '@/components/home/Abnormal.vue';
import Foot from '@/components/Foot.vue';
import '@/assets/prop.css';
import http from '@/common/api/request.js';
import { ChatClient } from "@qians/quake_ws_client-wasm"; //插件

import BScroll from '@better-scroll/core';
export default {
  name: 'HomeView',
  data: function data() {
    return {
      newsList: [//公告数据
      {
        id: 1,
        con: "微数场新功能即将上线，敬请期待~"
      }],
      cameraPopup: false,
      //监视器蒙尘状态
      addshow: false,
      pop1: false,
      //serch的pop们
      pop2: false,
      pop3: false,
      pop4: false,
      cameraList: [],
      device_type_name: [],
      //设备类型名称
      camlist: false,
      //设备列表是否更新
      ExceptionList: []
    };
  },
  components: {
    Serch: Serch,
    Camera: Camera,
    Foot: Foot,
    Abnormal: Abnormal
  },
  methods: {
    // //摄像头蒙尘状态
    change: function change(res) {
      this.cameraPopup = res;
    },
    // 头部加号状态
    addnewshow: function addnewshow(rq) {
      this.addshow = rq;
    },
    // addList
    closeTab: function closeTab() {
      if (this.addshow) {
        this.addshow = false;
        this.$refs.child.showTab(this.addshow);
      }
    },
    //serch的pop们
    serpop1: function serpop1(res) {
      this.pop1 = res;
    },
    serpop2: function serpop2(res) {
      this.pop2 = res;
    },
    serpop3: function serpop3(res) {
      this.pop3 = res;
    },
    serpop4: function serpop4(res) {
      this.pop4 = res;
    },
    // 设备列表是否更新
    updata: function updata(res) {
      this.camlist = res;
    },
    // 连接wasm
    chat: function chat() {
      // console.log("[Views: HomeView.vue]wait for init client")
      //FIXME: change k32212 to username(token)
      var that = this;
      ChatClient.init("k32212", 'levitas.quakeai.tech:31033', 'eye_h5', true);
      this.ChatClient = ChatClient;
      setTimeout(function () {
        console.log("[Views: HomeView.vue]chat client has been inited: ", that.ChatClient.get_group_chridren());
      }, 5000);
    },
    // 获取设备列表
    getCamerList: function getCamerList() {
      var _this = this;

      // console.log("123456")
      http.$axios({
        url: 'device/getDevices',
        method: "GET",
        params: {
          "org_id": this.$store.state.userorgid
        }
      }).then(function (res) {
        // console.log(res.data)
        _this.cameraList = res.data.data;
      });
    }
  },
  created: function created() {
    var _this2 = this;

    // this.chat()
    this.$store.commit('USER_ORG_ID', this.$route.query.UserOrgID), this.getCamerList();
    this.$nextTick(function () {
      // 获取设备类型名称
      http.$axios({
        url: 'https://1to2to3.cn/rds2/service/query',
        data: {
          "auth": 2,
          "project": "eagle_eye",
          "table": "sys_device_type",
          "conditions": {
            "fields": ["device_type_name"],
            "query": {}
          }
        }
      }).then(function (res) {
        // console.log(res.data)
        _this2.device_type_name = res.data.data;
      }), // 获取工控服务列表
      http.$axios({
        url: 'job/getJobs',
        method: 'GET',
        params: {
          "org_id": _this2.$store.state.userorgid
        }
      }).then(function (res) {
        _this2.ExceptionList = res.data.data;
      }).catch(function () {
        _this2.ExceptionList = [];
      });
    });
  },
  // mounted() {
  //  this.$nextTick(()=>{
  //   console.log(this.$store.state.userorgid)
  //  })
  // },
  watch: {
    camlist: function camlist(val) {
      var _this3 = this;

      if (val == true) {
        // 获取设备列表
        http.$axios({
          url: 'device/getDevices',
          method: "GET",
          params: {
            "org_id": this.$store.state.userorgid //机构ID
            //"page":1,
            //"pageSize":10,
            //     "device_type":1 设备类型

          }
        }).then(function (res) {
          // console.log(res.data.data)
          _this3.cameraList = res.data.data;
          _this3.camlist = false;
        });
      }
    },
    ExceptionList: function ExceptionList() {
      this.$nextTick(function () {
        var bs = new BScroll('.wrapper', {
          movable: true,
          zoom: true,
          probeType: 2,
          click: true
        });
      });
    }
  }
};
