import http from '@/common/api/request.js'; //插件

import BetterScroll from '@better-scroll/core';
export default {
  props: ['Exception'],

  data() {
    return {
      selected: '1',
      ExceptionList: [],
      aftertodyList: [],
      yesterdayList: []
    };
  },

  methods: {
    gounnormal(item) {
      let url1 = item.Videos[0].VideoUrl;
      let url2 = item.Videos[1].VideoUrl;
      let remark = item.Remark;
      let jobName = item.JobName;
      let createAt = item.CreateAt;
      let createBy = item.CreateBy;
      this.$router.push({
        path: "/unnormal",
        query: {
          'url1': url1,
          'url2': url2,
          'remark': remark,
          'jobName': jobName,
          'createAt': createAt,
          'createBy': createBy
        }
      });
    },

    aaa() {
      console.log(this.$refs.wapp.style);
    }

  },

  created() {
    // 今天获取工控服务列表
    http.$axios({
      url: 'job/getJobs',
      method: 'GET',
      params: {
        "org_id": this.$store.state.userorgid,
        "type": 1
      }
    }).then(res => {
      this.ExceptionList = res.data.data;
    }).catch(() => {
      this.ExceptionList = [];
    }), // 昨天获取工控服务列表
    http.$axios({
      url: 'job/getJobs',
      method: 'GET',
      params: {
        "org_id": this.$store.state.userorgid,
        "type": 2
      }
    }).then(res => {
      this.aftertodyList = res.data.data;
    }).catch(() => {
      this.aftertodyList = [];
    }); // 昨天之前获取工控服务列表

    http.$axios({
      url: 'job/getJobs',
      method: 'GET',
      params: {
        "org_id": this.$store.state.userorgid,
        "type": 3
      }
    }).then(res => {
      this.yesterdayList = res.data.data;
    }).catch(() => {
      this.yesterdayList = [];
    });
  },

  watch: {
    ExceptionList() {
      this.$nextTick(() => {
        new BetterScroll('.wrapper', {
          movable: true,
          zoom: true,
          probeType: 2,
          click: true
        });
      });
    },

    selected(n) {
      if (n == '2') {
        this.$nextTick(() => {
          new BetterScroll('.wrapper2', {
            movable: true,
            zoom: true,
            probeType: 2,
            click: true
          });
        });
      } else if (n == '3') {
        this.$nextTick(() => {
          new BetterScroll('.wrapper3', {
            movable: true,
            zoom: true,
            probeType: 2,
            click: true
          });
        });
      }
    }

  }
};
