export default {
  provide() {
    //刷新
    return {
      reload: this.reload
    };
  },

  data() {
    return {
      isRouterAlive: true
    };
  },

  created() {
    const height = document.documentElement.clientHeight;

    window.onload = function () {
      document.getElementById('app').style.height = `${height}px`;
    };
  },

  methods: {
    reload() {
      this.isRouterAlive = false;
      this.$nextTick(function () {
        this.isRouterAlive = true;
      });
    }

  }
};
