import request from '@/common/api/devrequest'





// 获取用户信息
export function userinfor(data){
	return request({
		url:'/user/'+data.userorgid+"/"+data.username ,
	})
}

// 普通超级管理员添加组织
export function addadmin(data){
  return request({
    url:"/user",
    method:'post',
    data
  })
}

//更新激活状态
export function ActivationState(data){
	return request({
		url:'/user',
		method:'put',
		data
	})
}

//获取组织下申请激活用户列表
export function adminList(data){
	return request({
		url:'/user/getOrgActivationList/'+data.userorgid+"/"+2,
		method:'get',
		data
	})
}

//获取组织下已激活用户列表
export function listEd(data){
	return request({
		url:'/user/getOrgActivationList/'+data.userorgid.org_id+"/"+1,
		method:'get',
		data
	})
}
