import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
	  userorgid:null,
	  username:'',
	  role_id:'',

  },
  getters: {
  },
  mutations: {
	  USER_IMG(state,user){
		  state.userorgid=user.UserOrgID
		  state.username=user.nickname
		  state.username=user.role_id
		  state.username=user.avatar
	  }
  },
  actions: {
  },
  modules: {
  }
})
