import BetterScroll from '@better-scroll/core';
export default {
  props: ['Exception'],

  data() {
    return {
      selected: '1',
      BetterScroll: '',
      TbetterScroll: ''
    };
  },

  methods: {
    gounnormal(item) {
      let url1 = item.Videos[0].VideoUrl;
      let url2 = item.Videos[1].VideoUrl;
      let remark = item.Remark;
      let jobName = item.JobName;
      let createAt = item.CreateAt;
      let createBy = item.CreateBy;
      this.$router.push({
        path: "/unnormal",
        query: {
          'url1': url1,
          'url2': url2,
          'remark': remark,
          'jobName': jobName,
          'createAt': createAt,
          'createBy': createBy
        }
      });
    }

  },

  mounted() {},

  watch: {
    Exception() {
      this.TbetterScroll = this.$nextTick(() => {
        new BetterScroll('.wrapper', {
          movable: true,
          zoom: true,
          probeType: 2,
          click: true
        });
      });
    }

  }
};
