import { getjournal } from '@/common/api/adminCenter';
export default {
  data() {
    return {
      jouranlList: []
    };
  },

  created() {
    getjournal({
      "appName": "eagle",
      "page": 1,
      "pageSize": 10,
      "username": localStorage.getItem('username'),
      "orgId": Number(this.$route.query.orgId)
    }).then(res => {
      this.jouranlList = res.data.data.content.reverse();
      this.jouranlList.length = 4;

      for (var i = 0; i < this.jouranlList.length; i++) {
        this.jouranlList[i].open = true;
      } // console.log(res)

    });
  },

  methods: {
    gojouranlList() {
      this.$router.push({
        name: 'journal'
      });
    },

    changeit(e) {
      let change = this.jouranlList[e];
      change.open = !change.open;
      this.$set(this.jouranlList, e, change);
    }

  }
};
