export default {
  data: function data() {
    return {
      startX: 0,
      //滑动开始
      endX: 0,
      //滑动结束
      lists: [{
        title: "标题1",
        imgUrl: "https://z3.ax1x.com/2021/05/18/gfwKHg.jpg",
        subheading: "副标题1",
        faddish: "爆款",
        price: "￥12.00"
      }, {
        title: "标题2",
        imgUrl: "https://z3.ax1x.com/2021/05/18/gfwKHg.jpg",
        subheading: "副标题2",
        faddish: "爆款",
        price: "￥58.00"
      }, {
        title: "标题3",
        imgUrl: "https://z3.ax1x.com/2021/05/18/gfwKHg.jpg",
        subheading: "副标题3",
        faddish: "爆款",
        price: "￥99.99"
      }, {
        title: "标题4",
        imgUrl: "https://z3.ax1x.com/2021/05/18/gfwKHg.jpg",
        subheading: "副标题4",
        faddish: "爆款",
        price: "￥88.32"
      }, {
        title: "标题5",
        imgUrl: "https://z3.ax1x.com/2021/05/18/gfwKHg.jpg",
        subheading: "副标题5",
        faddish: "爆款",
        price: "￥9999.99"
      }],
      popupVisible: false,
      //添加设备表单是否显示
      updata: false,
      //设备是否添加成功
      rootForm: {
        'device_ip': '',
        //设备ip
        // 'device_id':'',//设备
        'device_account': '',
        //设备账号
        'device_password': '',
        //设备密码
        'org_id': Number(this.$store.state.userorgid),
        //机构id
        'push_url': '',
        //推流直播地址
        'device_type': 1,
        //设备类型
        'state': 1,
        //状态
        'device_name': '',
        //设备名称
        "scope": ''
      },
      device_type_name: [],
      //设备类型名称
      cameraList: [],
      rules: {
        device_name: {
          rule: /^.+$/,
          msg: "设备名称不能为空"
        },
        device_ip: {
          rule: /^((\d|[1-9]\d|1\d\d|2[0-4]\d|25[0-5])\.){3}(\d|[1-9]\d|1\d\d|2[0-4]\d|25[0-5])$/,
          msg: "ip地址输入错误"
        },
        // device_id:{
        // 	rule:/^.+$/,
        // 	msg:"设备id不能为空"
        // },
        // device_account:{
        // 	rule:/^.+$/,
        // 	msg:"设备账号不能为空"
        // },
        device_password: {
          rule: /^.+$/,
          msg: "设备密码不能为空"
        },
        device_type: {
          rule: /^.+$/,
          msg: "设备类型不能为空"
        },
        device_id: {
          rule: /^.+$/,
          msg: "设备id不能为空"
        }
      }
    };
  },
  components: {
    Popupp: Popupp,
    Administrtab: Administrtab,
    Foot: Foot
  },
  created: function created() {
    var _this = this;

    // 获取设备列表
    http.$axios({
      url: 'device/getDevices',
      method: "GET",
      params: {
        "org_id": this.$store.state.userorgid
      }
    }).then(function (res) {
      _this.cameraList = res.data.data;
    });
    this.$nextTick(function () {
      // 获取设备类型名称
      http.$axios({
        url: 'https://1to2to3.cn/rds2/service/query',
        data: {
          "auth": 2,
          "project": "eagle_eye",
          "table": "sys_device_type",
          "conditions": {
            "fields": ["device_type_name"],
            "query": {}
          }
        }
      }).then(function (res) {
        // console.log(res.data)
        _this.device_type_name = res.data.data; // new BetterScroll('.wrapper', {
        //    movable: true,
        //    zoom: true,
        // 	probeType:2,
        // 	click:true
        //  })
      });
    });
  },
  methods: {
    // 添加设备表单
    formRec: function formRec() {
      this.popupVisible = true;
    },
    // 保存添加设备信息
    infPost: function infPost() {
      var _this2 = this;

      if (!this.validate('device_name')) return;
      if (!this.validate('device_ip')) return;
      if (!this.validate('device_id')) return; // if( !this.validate('device_account') ) return;

      if (!this.validate('device_password')) return;
      if (!this.validate('device_type')) return;
      this.rootForm.device_id = Number(this.rootForm.device_id);
      console.log(this.rootForm);
      http.$axios({
        url: '/device',
        data: this.rootForm
      }).then(function (res) {
        // console.log(res)
        Toast(res.data.result);
        _this2.popupVisible = false;
        _this2.updata = true;
      });
    },
    //验证信息提示
    validate: function validate(key) {
      var bool = true;

      if (!this.rules[key].rule.test(this.rootForm[key])) {
        //提示信息
        Toast(this.rules[key].msg);
        bool = false;
        return false;
      }

      return bool;
    },
    // 关闭蒙尘
    changVisible: function changVisible() {
      this.popupVisible = false;
    },
    // 向左滑动出现删除按钮时，点击商品信息区域取消删除
    oneself: function oneself() {
      if (this.checkSlide()) {
        this.restSlide();
      }
    },
    //滑动开始
    touchStart: function touchStart(e) {
      // 记录初始位置
      this.startX = e.touches[0].clientX;
    },
    //滑动结束
    touchEnd: function touchEnd(e) {
      // 当前滑动的父级元素
      var parentElement = e.currentTarget.parentElement; // 记录结束位置

      this.endX = e.changedTouches[0].clientX; // 左滑大于30距离删除出现

      if (parentElement.dataset.type == 0 && this.startX - this.endX > 30) {
        this.restSlide();
        parentElement.dataset.type = 1;
      } // 右滑


      if (parentElement.dataset.type == 1 && this.startX - this.endX < -30) {
        this.restSlide();
        parentElement.dataset.type = 0;
      }

      this.startX = 0;
      this.endX = 0;
    },
    //判断当前是否有滑块处于滑动状态
    checkSlide: function checkSlide() {
      var listItems = document.querySelectorAll(".li_vessel");

      for (var i = 0; i < listItems.length; i++) {
        if (listItems[i].dataset.type == 1) {
          return true;
        }
      }

      return false;
    },
    //复位滑动状态
    restSlide: function restSlide() {
      var listItems = document.querySelectorAll(".li_vessel"); // 复位

      for (var i = 0; i < listItems.length; i++) {
        listItems[i].dataset.type = 0;
      }
    },
    //删除数据信息
    remove: function remove(e) {
      // 当前索引值
      var index = e.currentTarget.dataset.index; // 复位

      this.restSlide(); // 删除数组lists中一个数据

      this.lists.splice(index, 1);
    }
  }
};
